 */
class ExampleUnitTest {
    @Test
    fun addition_isCorrect() {
        assertEquals(4, 2 + 2)
    }

    @Test
    fun `Parse result JSON with GSON`() {
        val adapter = RuntimeTypeAdapterFactory
            .of(Items::class.java, "cell_type", true)
            .registerSubtype(Company::class.java, "CELL_TYPE_COMPANY")
            .registerSubtype(HorizontalTheme::class.java, "CELL_TYPE_HORIZONTAL_THEME")
            .registerSubtype(Review::class.java, "CELL_TYPE_REVIEW")

        val gson = GsonBuilder()
            .registerTypeAdapterFactory(adapter)
            .create()

        val deserialized = try {
            Resource.Success<SearchResult>(gson.fromJson<SearchResult>(getCompanyListString(), SearchResult::class.java))
        }

        catch (e:Exception) {
            e.printStackTrace()
            Resource.Failure("Failed to parse JSON.")
        }

        val serialized = gson.toJson(deserialized)

        JSONAssert.assertEquals(gson.toJson(deserialized), serialized, false)

    }

